// define ADD, addMessage(), messageReducer(), and store here:
const ADD = 'ADD';

const addMessage = (msg) => {
    return {
        type: ADD,
        message: msg
    }
}

const messageReducer = (state = [], action) => {
    switch(action.type){
        case ADD:
            return state.concat(action.message);
            break;
        default:
            return state;
            break;
    }
}

const store = Redux.createStore(messageReducer);
