function titleCase(str) {
  let strArr = str.split(' ');
  console.log(strArr);
  for( let i = 0; i < strArr.length; i++) {
      let lower = strArr[i].toLowerCase();
      let chars = lower.split('');
      chars[0] = chars[0].toUpperCase();
      strArr[i] = chars.join('');
    }
  let strReturn = strArr.join(' ');
  return strReturn;
}

